import React, { useState } from 'react';
import { Container, Typography, Box, Chip, TextField, IconButton } from '@mui/material';
import SearchIcon from '@mui/icons-material/Search';
import { ThemeProvider, createTheme } from '@mui/material/styles';

const theme = createTheme({
  palette: {
    primary: {
      main: '#E1261C',
    },
  },
});

const tags = ['소셜미디어', '코로나19', 'AI', 'OTT', '인공지능', '유튜브'];

function App() {
  const [query, setQuery] = useState('');

  const handleSearch = () => {
    // Implement search functionality here
    console.log('Searching for:', query);
  };

  return (
    <ThemeProvider theme={theme}>
      <Container maxWidth="md">
        <Box sx={{ my: 4, textAlign: 'center' }}>
          <Typography variant="h4" component="h1" gutterBottom sx={{ fontWeight: 'bold' }}>
            더 똑똑하게 찾는 학술 정보
          </Typography>
          <Box sx={{ mt: 4, mb: 2 }}>
            <TextField
              fullWidth
              variant="outlined"
              placeholder="어떤 지식을 알고 싶으신가요?"
              value={query}
              onChange={(e) => setQuery(e.target.value)}
              InputProps={{
                endAdornment: (
                  <IconButton onClick={handleSearch} edge="end" color="primary">
                    <SearchIcon />
                  </IconButton>
                ),
              }}
              sx={{
                '& .MuiOutlinedInput-root': {
                  borderRadius: '30px',
                  '& fieldset': {
                    borderColor: 'primary.main',
                  },
                },
              }}
            />
          </Box>
          <Box sx={{ display: 'flex', justifyContent: 'center', flexWrap: 'wrap', gap: 1 }}>
            {tags.map((tag) => (
              <Chip
                key={tag}
                label={`# ${tag}`}
                onClick={() => setQuery(tag)}
                sx={{
                  borderRadius: '16px',
                  backgroundColor: 'transparent',
                  border: '1px solid',
                  borderColor: 'primary.main',
                  color: 'primary.main',
                  '&:hover': {
                    backgroundColor: 'primary.main',
                    color: 'white',
                  },
                }}
              />
            ))}
          </Box>
          <Typography variant="body2" sx={{ mt: 4, color: 'text.secondary' }}>
            프로토타입 테스트로, 신문방송학 관련 질문만 가능합니다.
          </Typography>
          <Typography variant="body2" sx={{ color: 'text.secondary' }}>
            주요 연구 키워드를 참고하여 질문해 보세요.
          </Typography>
        </Box>
      </Container>
    </ThemeProvider>
  );
}

export default App;
