import React from 'react';
import { Typography, Box, Paper, List, ListItem, ListItemText, Link } from '@mui/material';

function SearchResult({ result }) {
  // 유사도 점수가 특정 기준을 넘는 논문만 필터링
  const relevantPapers = result.sources.filter(paper => paper.similarity_score > 0.5); // 0.5는 예시 기준값입니다. 실제 기준값으로 조정해야 합니다.

  // 유사도 점수 기준으로 정렬
  relevantPapers.sort((a, b) => b.similarity_score - a.similarity_score);

  // 상위 N개만 선택 (예: 상위 5개)
  const topPapers = relevantPapers.slice(0, 5);

  return (
    <Box sx={{ display: 'flex' }}>
      <Box sx={{ flex: 2, pr: 2 }}>
        <Typography variant="body1" paragraph>
          {result.answer}
        </Typography>
      </Box>
      <Box sx={{ flex: 1 }}>
        <Paper elevation={3} sx={{ p: 2 }}>
          <Typography variant="h6" gutterBottom>논문 출처</Typography>
          {topPapers.length > 0 ? (
            <List>
              {topPapers.map((paper, index) => (
                <ListItem key={index} alignItems="flex-start" sx={{ px: 0 }}>
                  <ListItemText
                    primary={
                      <Typography variant="subtitle2" color="primary">
                        {index + 1}. {paper.title}
                      </Typography>
                    }
                    secondary={
                      <>
                        <Typography variant="body2" color="text.secondary">
                          {paper.authors}
                        </Typography>
                        <Link href={paper.url} target="_blank" rel="noopener noreferrer" color="primary" variant="body2">
                          논문 전문
                        </Link>
                        <Typography variant="body2" color="text.secondary">
                          {paper.abstract}
                        </Typography>
                        <Typography variant="body2">
                          {paper.journal || '저널명 없음'}
                        </Typography>
                      </>
                    }
                  />
                </ListItem>
              ))}
            </List>
          ) : (
            <Typography variant="body2" color="text.secondary">
              관련된 논문이 없습니다.
            </Typography>
          )}
        </Paper>
      </Box>
    </Box>
  );
}

export default SearchResult;
