import React from 'react';
import { Paper, Title, Text, Stack } from '@mantine/core';

const PaperList = ({ papers }) => {
  return (
    <Stack>
      <Title order={3}>관련 논문</Title>
      {papers.map((paper, index) => (
        <Paper key={index} padding="md" shadow="xs">
          <Title order={4}>{paper.title}</Title>
          <Text>저자: {paper.authors}</Text>
          <Text>
            URL: <a href={paper.url} target="_blank" rel="noopener noreferrer">{paper.url}</a>
          </Text>
          <Text>초록: {paper.abstract}</Text>
        </Paper>
      ))}
    </Stack>
  );
};

export default PaperList;
