import React, { useState } from 'react';
import { Container, CssBaseline, Typography, Box, Grid } from '@mui/material';
import SearchBar from './components/SearchBar';
import GPTSummary from './components/GPTSummary';
import PaperList from './components/PaperList';

function App() {
  const [searchResult, setSearchResult] = useState(null);
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState(null);

  const handleSearch = async (query) => {
    setIsLoading(true);
    setError(null);
    try {
      const response = await fetch('http://localhost:8000/search', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ question: query }),
      });
      if (!response.ok) {
        throw new Error('Search request failed');
      }
      const data = await response.json();
      setSearchResult(data);
    } catch (err) {
      setError(err.message);
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <>
      <CssBaseline />
      <Container maxWidth="lg">
        <Box sx={{ my: 4 }}>
          <Typography variant="h4" component="h1" gutterBottom align="center">
            더 똑똑하게 찾는 학술 정보
          </Typography>
          <SearchBar onSearch={handleSearch} />
          {isLoading && <Typography align="center">Loading...</Typography>}
          {error && <Typography color="error" align="center">{error}</Typography>}
          {searchResult && (
            <Grid container spacing={2} sx={{ mt: 2 }}>
              <Grid item xs={12} md={8}>
                <GPTSummary summary={searchResult.answer} />
              </Grid>
              <Grid item xs={12} md={4}>
                <PaperList papers={searchResult.sources} />
              </Grid>
            </Grid>
          )}
        </Box>
      </Container>
    </>
  );
}

export default App;
