import React from 'react';
import { Typography, Box, Paper, List, ListItem, ListItemText, Link } from '@mui/material';

function SearchResult({ result }) {
  const { answer, sources } = result;

  // 관련성 점수 임계값 (이 값은 백엔드와 협의하여 조정해야 합니다)
  const relevanceThreshold = 0.5;

  // 관련성 점수가 임계값을 넘는 논문만 필터링
  const relevantSources = sources.filter(source => source.relevance_score > relevanceThreshold);

  return (
    <Box sx={{ display: 'flex' }}>
      <Box sx={{ flex: 2, pr: 2 }}>
        <Typography variant="body1" paragraph>
          {answer}
        </Typography>
      </Box>
      {relevantSources.length > 0 ? (
        <Box sx={{ flex: 1 }}>
          <Paper elevation={3} sx={{ p: 2 }}>
            <Typography variant="h6" gutterBottom>관련 논문</Typography>
            <List>
              {relevantSources.map((paper, index) => (
                <ListItem key={index} alignItems="flex-start" sx={{ px: 0 }}>
                  <ListItemText
                    primary={
                      <Typography variant="subtitle2" color="primary">
                        {index + 1}. {paper.title}
                      </Typography>
                    }
                    secondary={
                      <>
                        <Typography variant="body2" color="text.secondary">
                          저자: {paper.authors}
                        </Typography>
                        <Link href={paper.url} target="_blank" rel="noopener noreferrer" color="primary" variant="body2">
                          논문 전문
                        </Link>
                        <Typography variant="body2" color="text.secondary">
                          초록: {paper.abstract}
                        </Typography>
                      </>
                    }
                  />
                </ListItem>
              ))}
            </List>
          </Paper>
        </Box>
      ) : (
        <Box sx={{ flex: 1 }}>
          <Typography variant="body1" color="text.secondary">
            관련된 논문이 없습니다.
          </Typography>
        </Box>
      )}
    </Box>
  );
}

export default SearchResult;
