import React from 'react';
import { Typography, Paper, List, ListItem, ListItemText, CircularProgress, Alert } from '@mui/material';

function ResultDisplay({ result, isLoading, error }) {
  if (isLoading) {
    return <CircularProgress />;
  }

  if (error) {
    return <Alert severity="error">{error}</Alert>;
  }

  if (!result) {
    return null;
  }

  return (
    <div>
      <Paper elevation={3} sx={{ p: 2, mt: 2 }}>
        <Typography variant="h6">Summary:</Typography>
        <Typography paragraph>{result.answer}</Typography>
      </Paper>
      <Paper elevation={3} sx={{ p: 2, mt: 2 }}>
        <Typography variant="h6">Related Papers:</Typography>
        <List>
          {result.sources.map((source, index) => (
            <ListItem key={index}>
              <ListItemText
                primary={source.title}
                secondary={
                  <>
                    <Typography component="span" variant="body2" color="text.primary">
                      Authors: {source.authors}
                    </Typography>
                    <br />
                    <a href={source.url} target="_blank" rel="noopener noreferrer">
                      View Paper
                    </a>
                  </>
                }
              />
            </ListItem>
          ))}
        </List>
      </Paper>
    </div>
  );
}

export default ResultDisplay;
