import React, { useState } from 'react';
import { Container, Typography, Box, TextField, IconButton, Divider } from '@mui/material';
import SearchIcon from '@mui/icons-material/Search';
import { ThemeProvider, createTheme } from '@mui/material/styles';
import SearchResult from './components/SearchResult';
import NoResult from './components/NoResult';

const theme = createTheme({
  palette: {
    primary: {
      main: '#E1261C',
    },
  },
});

function App() {
  const [query, setQuery] = useState('');
  const [searchResult, setSearchResult] = useState(null);
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState(null);

  const handleSearch = async () => {
    setIsLoading(true);
    setError(null);
    try {
      const response = await fetch('http://localhost:8000/search', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ question: query }),
      });
      if (!response.ok) {
        throw new Error('Search request failed');
      }
      const data = await response.json();
      setSearchResult(data);
    } catch (err) {
      setError(err.message);
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <ThemeProvider theme={theme}>
      <Container maxWidth="lg">
        <Box sx={{ my: 4 }}>
          <Box sx={{ display: 'flex', alignItems: 'center', mb: 2 }}>
            <Typography variant="h5" component="h1" sx={{ fontWeight: 'bold' }}>
              {query || ""}
            </Typography>
          </Box>
          <Divider sx={{ borderColor: 'primary.main', borderWidth: 2 }} />
          <Box sx={{ mt: 2, mb: 4 }}>
            <TextField
              fullWidth
              variant="outlined"
              placeholder="어떤 지식을 알고 싶으신가요?"
              value={query}
              onChange={(e) => setQuery(e.target.value)}
              onKeyPress={(e) => {
                if (e.key === 'Enter') {
                  handleSearch();
                }
              }}
              InputProps={{
                endAdornment: (
                  <IconButton onClick={handleSearch} edge="end" color="primary">
                    <SearchIcon />
                  </IconButton>
                ),
              }}
            />
          </Box>
          {isLoading && <Typography align="center">Loading...</Typography>}
          {error && <Typography color="error" align="center">{error}</Typography>}
          {searchResult ? (
            searchResult.sources.length > 0 ? (
                <SearchResult result={searchResult} />
            ) : (
                <NoResult />
            )
           ) : null}
        </Box>
      </Container>
    </ThemeProvider>
  );
}

export default App;
