import React from 'react';
import { Typography, Box, Paper, List, ListItem, ListItemText, Link } from '@mui/material';

function SearchResult({ result }) {
  // 유사도 점수가 특정 기준을 넘는 논문만 필터링
  const similarityThreshold = 0.5; // 이 값은 백엔드와 협의하여 설정해야 합니다
  const relevantPapers = result.sources.filter(paper => paper.similarity_score > similarityThreshold);

  return (
    <Box sx={{ display: 'flex' }}>
      <Box sx={{ flex: 2, pr: 2 }}>
        <Typography variant="body1" paragraph>
          {result.answer}
        </Typography>
      </Box>
      {relevantPapers.length > 0 && (
        <Box sx={{ flex: 1 }}>
          <Paper elevation={3} sx={{ p: 2 }}>
            <Typography variant="h6" gutterBottom>논문 출처</Typography>
            <List>
              {relevantPapers.map((paper, index) => (
                <ListItem key={index} alignItems="flex-start" sx={{ px: 0 }}>
                  <ListItemText
                    primary={
                      <Typography variant="subtitle2" color="primary">
                        {index + 1}. {paper.title}
                      </Typography>
                    }
                    secondary={
                      <>
                        <Typography variant="body2" color="text.secondary">
                          {paper.authors}
                        </Typography>
                        <Link href={paper.url} target="_blank" rel="noopener noreferrer" color="primary" variant="body2">
                          논문 전문
                        </Link>
                        <Typography variant="body2" color="text.secondary">
                          {paper.abstract}
                        </Typography>
                        <Typography variant="body2">
                          {paper.journal || '저널명 없음'}
                        </Typography>
                      </>
                    }
                  />
                </ListItem>
              ))}
            </List>
          </Paper>
        </Box>
      )}
    </Box>
  );
}

export default SearchResult;
